def cipher(plaintext, key):
    res = ""
    for ch in plaintext:
        res += chr(ord(ch) + key)
    return res

plaintext = input("Input plaintext:\n")
key = int(input("Input key (number by which to shift the characters in the text):\n"))
print("Result: " + cipher(plaintext,key))

